async def dashboard_love_style():
    return """
        <style>

        /* --- Features Section --- */
        .features-section {
            width: 100%;
            padding-bottom:5vw;
            text-align: center;
        }

        .section-title {
            font-size: 4.5vw;
            /* Responsive font size based on viewport width */
            font-weight: 700;
            margin-bottom: 10vw;
            letter-spacing: -0.02em;
        }

        .section-title .highlight {
            color: #5FC7FB;
            /* Blue highlight color */
        }

        /* --- Features Grid Layout --- */
        .features-grid {
            display: grid;
            /* Desktop: 4 columns */
            grid-template-columns: repeat(4, 1fr);;
            max-width: 75vw;
            /* Limit max width for very large screens */
            margin: 0 auto;
            justify-items: center; /* Center cards in the grid */
        }

        /* --- Feature Card --- */
        .feature-card {
            display: flex;
            flex-direction: column;
            align-items: flex-start;
            text-align: left;
            padding: 1.68vw;
            gap: 1.01vw;
            border-radius: 1.01vw;
            width: 13.9vw;
            height: 16vw; /* Maintained aspect ratio */
            border: 0.1vw solid #5FC7FB;
            background: transparent;
            border-bottom: none;
            position: relative;
            box-sizing: border-box;
        }

        .feature-card::after {
            content: '';
            position: absolute;
            left: 0;
            right: 0;
            bottom: 0;
            height: 0.1vw;
            background: linear-gradient(to right, transparent, #5FC7FB, transparent);
        }

        .icon-container {
            width: 2.83vw;
            height: 2.83vw;
            display: flex;
            justify-content: center;
            align-items: center;
            border: 0.08vw solid #5FC7FB;
            border-radius: 0.54vw;
        }

        .icon-container svg {
            width: 1.48vw;
            height: 1.48vw;
            fill: #5FC7FB;
        }

        .feature-card h3 {
            font-size: 1.14vw;
            font-weight: 500;
            line-height: 1.2;
            text-transform: none;
        }

        .feature-card p {
            font-size: 0.74vw;
            font-weight: 300;
            line-height: 1.25;
            color: rgba(254, 254, 254, 0.78);
            text-transform: none;
            margin-top: 1.01vw;
        }

        /* --- Responsive Adjustments --- */

        /* Tablet View */
        @media (max-width: 1024px) {
            .features-grid {
                /* 2 columns for tablets */
                grid-template-columns: repeat(2, 1fr);
                gap: 4vw;
                max-width: 90vw;
            }

            .section-title {
                font-size: 6vw;
            }

            .feature-card {
                width: 40vw; /* Adjust card width for tablet */
                height: 45.8vw; /* Adjust height to maintain aspect ratio */
                padding: 4vw;
                border-radius: 2vw;
                gap: 2vw;
            }

            .icon-container {
                width: 8vw;
                height: 8vw;
                border-radius: 1.5vw;
            }

            .icon-container svg {
                width: 4vw;
                height: 4vw;
            }

            .feature-card h3 {
                font-size: 3vw;
            }

            .feature-card p {
                font-size: 2vw;
            }
        }

        /* Mobile View */
        @media (max-width: 640px) {
            .features-grid {
                /* 1 column for mobile */
                grid-template-columns: 1fr;
                gap: 8vw;
                max-width: 85vw;
            }

            .section-title {
                font-size: 8vw;
                margin-bottom: 10vh;
            }

            .feature-card {
                width: 100%; /* Full width on mobile */
                height: auto; /* Auto height */
                padding: 6vw;
                gap: 5vw;
                border-radius: 3vw;
            }

            .icon-container {
                width: 12vw;
                height: 12vw;
                border-radius: 2.5vw;
            }

            .icon-container svg {
                width: 6vw;
                height: 6vw;
            }

            .feature-card h3 {
                font-size: 5.5vw;
            }

            .feature-card p {
                font-size: 4vw;
            }
        }
    </style>

"""
